      &amp;#125;)
    &amp;#125;,
    methods: &amp;#123;
      toAddressList () &amp;#123;
        this.$router.push(&#39;/orderAddress?time=&#39; + this.time)
      &amp;#125;,
      pay () &amp;#123;
        Toast(&#39;正在努力开发中...&#39;)
      &amp;#125;
    &amp;#125;
  &amp;#125;)
&amp;lt;/script&amp;gt;

